mapper.select:
SELECT
FROM gacetadata g, gacetadatav_viena vv, gacetadata_viena gv, gacetadata_clase c where gv.solicitud=g.solicitud and gv.solicitud=c.solicitud and vv.categoria=gv.categoria and vv.division=gv.division and vv.seccion=gv.seccion and vv.solicitud=#{solicitud} and c.clase=#{clase}
and g.F_Registro = DATE_FORMAT(#{fechaPub1},'%Y/%m/%d')
and g.F_Registro between DATE_FORMAT(#{fechaPub1},'%Y/%m/%d') and DATE_FORMAT(#{fechaPub2},'%Y/%m/%d')
group by gv.solicitud,vv.solicitud order by vv.secuencia ,ID_PUBL_PCT desc,count(*) desc limit #{limit}

		SELECT 

		FROM 
			gacetadata



                        idRegistro=#{idRegistro}


                      AND  Tipo=#{tipo}


                      AND  Subtipo=#{subtipo}


                      AND  Registro =#{registro}


                      AND  Solicitud=#{solicitud}


                         AND  upper(gaceta) LIKE upper(concat('%',#{gaceta},'%'))


                        and f_registro = DATE_FORMAT(#{fRegistro},'%Y/%m/%d') 


                        and presentacion = DATE_FORMAT(#{presentacion},'%Y/%m/%d') 


                        AND upper(titulo) LIKE upper(concat('%',#{titulo},'%'))


                       AND  upper(Titular) LIKE upper(concat('%',#{titular},'%'))


                      AND  Apoderado LIKE concat('%',#{apoderado},'%')

		SELECT 

		FROM 
			gacetadatav



                        idRegistro=#{idRegistro}


                      AND  Tipo=#{tipo}


                      AND  Subtipo=#{subtipo}


                      AND  Registro=#{registro}


                      AND  Solicitud=#{solicitud}


                       AND upper(titulo) LIKE upper(concat('%',#{titulo},'%'))


                      AND  upper(Titular) LIKE upper(concat('%',#{titular},'%'))


                      AND  upper(Apoderado) LIKE upper(concat('%',#{apoderado},'%'))


                      AND  idregistro in (select idregistro from gacetadatav_usuario where id_usuario=#{pagina} )

mapper.insert:
INSERT INTO gacetadata( ) VALUES( null, #{tipo}, #{subtipo}, #{registro}, #{solicitud}, #{titulo}, #{titular}, #{gaceta}, #{pagina}, #{clase}, #{prioridad}, #{idPublPCT}, #{idSolicitudPCT}, #{pais}, #{presentacion}, #{fRegistro}, #{vigencia}, #{tipoGaceta}, #{apoderado}, #{diseno}, #{titularDir}, #{presentacion2}, #{fRegistro2}
)
SELECT last_insert_id() AS idRegistro

INSERT INTO gacetadatav( ) VALUES( null, #{tipo}, #{subtipo}, #{registro}, #{solicitud}, #{titulo}, #{titular}, #{gaceta}, #{pagina}, #{clase}, #{prioridad}, #{idPublPCT}, #{idSolicitudPCT}, #{pais}, #{presentacion}, #{fRegistro}, #{vigencia}, #{tipoGaceta}, #{apoderado}, #{diseno}, #{titularDir}, #{presentacion2}, #{fRegistro2}
)
SELECT last_insert_id() AS idRegistro
